// App.js
import React, { useState } from "react";
import {
  AppContainer,
  InputContainer,
  TodoInput,
  AddButton,
  TodoListContainer,
  TodoItem,
  DeleteButton,
} from "./Appstyles.js";

function App() {
  const [tasks, setTasks] = useState([]);
  const [newTask, setNewTask] = useState("");

  const addTask = () => {
    if (newTask.trim() !== "") {
      setTasks([...tasks, { id: Date.now(), text: newTask }]);
      setNewTask("");
    }
  };

  const deleteTask = (taskId) => {
    const updatedTasks = tasks.filter((task) => task.id !== taskId);
    setTasks(updatedTasks);
  };

  return (
    <AppContainer>
      <h1>Todo List App</h1>
      <InputContainer>
        <TodoInput
          type="text"
          value={newTask}
          onChange={(e) => setNewTask(e.target.value)}
          placeholder="Add a new task"
        />
        <AddButton onClick={addTask}>Add Task</AddButton>
      </InputContainer>
      <TodoListContainer>
        {tasks.map((task) => (
          <TodoItem key={task.id}>
            {task.text}
            <DeleteButton onClick={() => deleteTask(task.id)}>
              Delete
            </DeleteButton>
          </TodoItem>
        ))}
      </TodoListContainer>
    </AppContainer>
  );
}

export default App;
