import React, { useState } from "react";
import "bootstrap/dist/css/bootstrap.min.css";
import "./App.css";

const TodoApp = () => {
  const [tasks, setTasks] = useState([]);
  const [newTask, setNewTask] = useState("");
  const [selectedTask, setSelectedTask] = useState(null);
  const [wordToUpdate, setWordToUpdate] = useState("");
  const [replacementWord, setReplacementWord] = useState("");

  const handleCreate = () => {
    setTasks([...tasks, newTask]);
    setNewTask("");
  };

  const handleUpdate = () => {
    if (
      selectedTask !== null &&
      wordToUpdate.trim() !== "" &&
      replacementWord.trim() !== ""
    ) {
      const updatedTasks = tasks.map((task, index) => {
        if (index === selectedTask) {
          const updatedTask = task.replace(
            new RegExp(wordToUpdate, "g"),
            replacementWord
          );
          return updatedTask;
        }
        return task;
      });

      setTasks(updatedTasks);
      setNewTask("");
      setSelectedTask(null);
      setWordToUpdate("");
      setReplacementWord("");
    }
  };

  const handleDelete = () => {
    if (selectedTask !== null) {
      const updatedTasks = tasks.filter((_, index) => index !== selectedTask);
      setTasks(updatedTasks);
      setNewTask("");
      setSelectedTask(null);
      setWordToUpdate("");
      setReplacementWord("");
    }
  };

  return (
    <div className="container mt-5">
      <h1>To-Do List Application</h1>
      <div className="mb-3">
        <input
          type="text"
          className="form-control"
          placeholder="Enter task"
          value={newTask}
          onChange={(e) => setNewTask(e.target.value)}
        />
      </div>
      <div className="mb-3">
        <input
          type="text"
          className="form-control"
          placeholder="Word to update"
          value={wordToUpdate}
          onChange={(e) => setWordToUpdate(e.target.value)}
        />
        <input
          type="text"
          className="form-control"
          placeholder="Replacement word"
          value={replacementWord}
          onChange={(e) => setReplacementWord(e.target.value)}
        />
        <button className="btn btn-primary mr-2" onClick={handleCreate}>
          Create
        </button>
        <button className="btn btn-warning mr-2" onClick={handleUpdate}>
          Update
        </button>
        <button className="btn btn-danger" onClick={handleDelete}>
          Delete
        </button>
      </div>
      <ul className="list-group">
        {tasks.map((task, index) => (
          <li
            key={index}
            className={`list-group-item ${
              selectedTask === index ? "active" : ""
            }`}
            onClick={() => setSelectedTask(index)}
          >
            {task}
          </li>
        ))}
      </ul>
    </div>
  );
};

export default TodoApp;
