import React, { useState } from 'react';
import 'bootstrap/dist/css/bootstrap.min.css';
import './App.css';

const TodoApp = () => {
  const [tasks, setTasks] = useState([]);
  const [newTask, setNewTask] = useState('');
  const [selectedTask, setSelectedTask] = useState(null);
  const [updateText, setUpdateText] = useState('');

  const handleCreate = () => {
    setTasks([...tasks, newTask]);
    setNewTask('');
  };

  const handleUpdate = () => {
    if (selectedTask !== null) {
      if (updateText.trim() !== '') {
        const updatedTasks = [...tasks];
        const originalTask = updatedTasks[selectedTask];
        const updatedTask = originalTask.replace(updateText, newTask);
        updatedTasks[selectedTask] = updatedTask;
        setTasks(updatedTasks);
        setNewTask('');
        setUpdateText('');
      } else {
        alert('Please enter the text to update in the task.');
      }
    }
  };

  const handleDelete = () => {
    if (selectedTask !== null) {
      const updatedTasks = tasks.filter((_, index) => index !== selectedTask);
      setTasks(updatedTasks);
      setNewTask('');
      setSelectedTask(null);
    }
  };

  return (
    <div className="container mt-5">
      <h1>To-Do List Application</h1>
      <div className="mb-3">
        <input
          type="text"
          className="form-control"
          placeholder="Enter task"
          value={newTask}
          onChange={(e) => setNewTask(e.target.value)}
        />
      </div>
      <div className="mb-3">
        <input
          type="text"
          className="form-control mr-2"
          placeholder="Text to update"
          value={updateText}
          onChange={(e) => setUpdateText(e.target.value)}
        />
        <button className="btn btn-warning mr-2" onClick={handleUpdate}>
          Update
        </button>
        <button className="btn btn-primary" onClick={handleCreate}>
          Create
        </button>
      </div>
      <ul className="list-group">
        {tasks.map((task, index) => (
          <li
            key={index}
            className={`list-group-item ${selectedTask === index ? 'active' : ''}`}
            onClick={() => setSelectedTask(index)}
          >
            {task}
          </li>
        ))}
      </ul>
    </div>
  );
};

export default TodoApp;
